import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  useLocation,
} from "react-router-dom";
import Navbar from "./components/Navbar";
import HomePage from "./components/HomePage";
import TransactionForm from "./components/TransactionForm";
import ETransferForm from "./components/ETransferForm";
import Login from "./components/Login";
import SignUp from "./components/SignUp";
import Footer from "./components/Footer";
import Dashboard from "./components/Dashboard";
import "bootstrap/dist/css/bootstrap.min.css";
import './App.css';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const authState = localStorage.getItem("isAuthenticated");
    if (authState) {
      setIsAuthenticated(JSON.parse(authState));
    }
  }, []);

  const handleLogin = (accountNumber) => {
    setIsAuthenticated(true);
    localStorage.setItem("isAuthenticated", true);
    localStorage.setItem("accountNumber", accountNumber);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("isAuthenticated");
    localStorage.removeItem("accountNumber");
  };

  const ProtectedRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />;
  };

  const RouteChangeHandler = () => {
    const location = useLocation();

    useEffect(() => {
      localStorage.setItem("lastVisitedRoute", location.pathname);
    }, [location]);

    return null;
  };

  return (
    <Router>
      <div>
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />
        <div className="container mt-4">
          <RouteChangeHandler />
          <Routes>
            <Route
              path="/"
              element={
                isAuthenticated ? <Navigate to="/home" /> : <Dashboard />
              }
            />
            <Route
              path="/home"
              element={
                <ProtectedRoute
                  element={<HomePage />}
                />
              }
            />
            <Route
              path="/deposit"
              element={
                <ProtectedRoute
                  element={<TransactionForm type="deposit" />}
                />
              }
            />
            <Route
              path="/withdraw"
              element={
                <ProtectedRoute
                  element={<TransactionForm type="withdraw" />}
                />
              }
            />
            <Route
              path="/etransfer"
              element={
                <ProtectedRoute
                  element={<ETransferForm />}
                />
              }
            />
            <Route
              path="/login"
              element={<Login onLogin={handleLogin} isAuthenticated={isAuthenticated} />}
            />
            <Route path="/signup" element={<SignUp />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
