// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import Navbar from './Navbar';
import HomePage from './HomePage';
import TransactionForm from './TransactionForm';
import ETransferForm from './ETransferForm';
import Login from './Login';
import SignUp from './SignUp';
import Footer from './Footer';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [balances, setBalances] = useState({
    checking: 0,
    savings: 0,
    creditCard: 0,
    investment: 0,
  });
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const contacts = [
    'Abhishek Dhiman', 'Sukhjeet Singh', 'Arpan Silwal', 'Nawaz Chowdhry', 
    'Surjeet Singh', 'Sejal Josan', 'Nabdeep Kaur', 'Jasdeep Kaur', 'Riya Mankotia'
  ];

  useEffect(() => {
    const authState = localStorage.getItem('isAuthenticated');
    if (authState) {
      setIsAuthenticated(JSON.parse(authState));
      const accountNumber = localStorage.getItem('accountNumber');
      if (accountNumber) {
        loadTransactions(accountNumber);
      }
    }

    const lastVisitedRoute = localStorage.getItem('lastVisitedRoute');
    if (lastVisitedRoute) {
      window.history.replaceState(null, '', lastVisitedRoute);
    }
  }, []);

  const saveTransactionToAPI = async (transaction) => {
    try {
      const response = await fetch('/api/saveTransaction', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(transaction)
      });
      if (!response.ok) {
        throw new Error('Failed to save transaction');
      }
    } catch (error) {
      console.error('Error saving transaction:', error);
    }
  };

  const loadTransactions = async (accountNumber) => {
    try {
      const response = await fetch(`/api/getTransactions?accountNumber=${accountNumber}`);
      if (response.ok) {
        const transactions = await response.json();
        setTransactions(transactions);
        const newBalances = { checking: 0, savings: 0, creditCard: 0, investment: 0 };
        transactions.forEach(transaction => {
          if (transaction.type === 'deposit') {
            newBalances[transaction.accountType] += transaction.amount;
          } else if (transaction.type === 'withdraw' || transaction.type === 'etransfer') {
            newBalances[transaction.accountType] -= transaction.amount;
          }
        });
        setBalances(newBalances);
      } else {
        throw new Error('Failed to load transactions');
      }
    } catch (error) {
      console.error('Error loading transactions:', error);
    }
  };

  const handleTransaction = ({ amount, type, accountType }) => {
    const accountNumber = localStorage.getItem('accountNumber');
    const transaction = {
      type,
      accountNumber,
      amount,
      accountType,
      timestamp: new Date().toLocaleString()
    };

    let newBalance;
    if (type === 'deposit') {
      newBalance = balances[accountType] + amount;
    } else if (type === 'withdraw') {
      if (balances[accountType] < amount) {
        alert('Insufficient balance');
        return;
      }
      newBalance = balances[accountType] - amount;
    }

    setBalances({
      ...balances,
      [accountType]: newBalance
    });

    setTransactions(prevTransactions => [...prevTransactions, transaction]);
    saveTransactionToAPI(transaction);  // Save transaction to API
  };

  const handleETransfer = ({ fromAccount, toContact, amount }) => {
    const accountNumber = toContact
    if (balances[fromAccount] < amount) {
      alert('Insufficient balance');
      return;
    }

    const newBalance = balances[fromAccount] - amount;

    const transaction = {
      type: 'etransfer',
      accountNumber,  // Use account number from local storage
      amount,
      accountType: fromAccount,
      timestamp: new Date().toLocaleString()
    };

    setBalances({
      ...balances,
      [fromAccount]: newBalance
    });

    setTransactions(prevTransactions => [...prevTransactions, transaction]);
    saveTransactionToAPI(transaction);  // Save transaction to API
  };

  const handleLogin = (accountNumber) => {
    setIsAuthenticated(true);
    localStorage.setItem('isAuthenticated', true);
    localStorage.setItem('accountNumber', accountNumber);
    loadTransactions(accountNumber);  // Load transactions for logged-in user
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('accountNumber'); // Clear account number on logout
    setTransactions([]);  // Clear transactions on logout
    setBalances({ checking: 0, savings: 0, creditCard: 0, investment: 0 });  // Reset balances
  };

  const ProtectedRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />;
  };

  const RouteChangeHandler = () => {
    const location = useLocation();

    useEffect(() => {
      localStorage.setItem('lastVisitedRoute', location.pathname);
    }, [location]);

    return null;
  };

  return (
    <Router>
      <div>
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />
        <div className="container mt-4">
          <RouteChangeHandler />
          <Routes>
            <Route path="/" element={<ProtectedRoute element={<HomePage balances={balances} transactions={transactions} />} />} />
            <Route path="/deposit" element={<ProtectedRoute element={<TransactionForm type="deposit" onSubmit={handleTransaction} />} />} />
            <Route path="/withdraw" element={<ProtectedRoute element={<TransactionForm type="withdraw" onSubmit={handleTransaction} />} />} />
            <Route path="/etransfer" element={<ProtectedRoute element={<ETransferForm contacts={contacts} onSubmit={handleETransfer} />} />} />
            <Route path="/login" element={<Login onLogin={handleLogin} isAuthenticated={isAuthenticated} />} />
            <Route path="/signup" element={<SignUp />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
