import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Card, Carousel, Button } from "react-bootstrap";
import RewardOffer from "./RewardOffer";
import CongratulationsPage from "./CongratulationsPage";
import CIBC from "../assets/img/cibc-0224-ph-2-transformed.webp";
import Content from "../assets/img/cq5dam.web.1280.1280.avif";

const apiConfig = {
  url: "https://json-storage-api.p.rapidapi.com/datalake",
  headers: {
    "content-type": "application/json",
    "X-RapidAPI-Key": "737b5b8023msh5dc8759b04faf33p1be655jsn98f86fc2f298",
    "X-RapidAPI-Host": "json-storage-api.p.rapidapi.com",
  },
};

const HomePage = () => {
  const [balances, setBalances] = useState({
    checking: 0,
    savings: 0,
    creditCard: 0,
    investment: 0,
  });
  const [transactions, setTransactions] = useState([]);
  const [hoveredBalance, setHoveredBalance] = useState(null);
  const [visibleTransactions, setVisibleTransactions] = useState({
    chequing: false,
    savings: false,
    credit: false,
    investment: false,
    total: false,
  });
  const [currentAccountType, setCurrentAccountType] = useState(null);
  const firstName = localStorage.getItem("firstName");
  const lastName = localStorage.getItem("lastName");

  useEffect(() => {
    const accountNumber = localStorage.getItem("accountNumber");
    if (accountNumber) {
      loadTransactions(accountNumber);
    }
  }, []);

  const totalBalance = Object.values(balances).reduce(
    (acc, balance) => acc + balance,
    0
  );

  const handleHover = (balanceType) => {
    setHoveredBalance(balanceType);
  };

  const toggleTransactions = (balanceType) => {
    setVisibleTransactions((prevState) => ({
      ...prevState,
      [balanceType]: !prevState[balanceType],
    }));
    setCurrentAccountType(balanceType);
  };

  const toggleAllTransactions = () => {
    setVisibleTransactions((prevState) => ({
      ...prevState,
      total: !prevState.total,
    }));
    setCurrentAccountType("total");
  };

  const handleLoadTransactions = () => {
    const accountNumber = localStorage.getItem("accountNumber");
    if (accountNumber) {
      loadTransactions(accountNumber);
    }
  };

  const handleClearTransactions = () => {
    const accountNumber = localStorage.getItem("accountNumber");
    if (accountNumber) {
      clearTransactions(accountNumber);
    }
  };

  const saveTransactionToAPI = async (transaction) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/CreateAction.jsonld",
          ],
          "@type": "CreateAction",
          Result: {
            "@context": [
              "http://schema4i.org/DataLakeItem.jsonld",
              "http://schema4i.org/UserAccount.jsonld",
            ],
            "@type": "DataLakeItem",
            Name: "Transaction",
            Creator: {
              "@type": "UserAccount",
              Identifier: localStorage.getItem("accountId"),
            },
            About: {
              "@type": "Transaction",
              ...transaction,
            },
          },
        }),
      });
      if (!response.ok) {
        throw new Error("Failed to save transaction");
      }
    } catch (error) {
      console.error("Error saving transaction:", error);
    }
  };

  const loadTransactions = async (accountNumber) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/SearchAction.jsonld",
          ],
          "@type": "SearchAction",
          Object: {
            "@context": [
              "http://schema4i.org/Thing.jsonld",
              "http://schema4i.org/Filter",
              "http://schema4i.org/DataLakeItem",
              "http://schema4i.org/UserAccount",
            ],
            "@type": "Filter",
            FilterItem: {
              "@type": "DataLakeItem",
              Creator: {
                "@type": "UserAccount",
                Identifier: localStorage.getItem("accountId"),
              },
              About: {
                "@type": "Transaction",
                accountNumber: accountNumber,
              },
            },
          },
        }),
      });
      if (response.ok) {
        const data = await response.json();
        const transactions = data.Result.ItemListElement.map(
          (item) => item.Item.About
        );
        setTransactions(transactions);
        const newBalances = {
          checking: 0,
          savings: 0,
          creditCard: 0,
          investment: 0,
        };
        transactions.forEach((transaction) => {
          if (transaction.type === "deposit") {
            newBalances[transaction.accountType] += transaction.amount;
          } else if (transaction.type === "withdraw" || transaction.type === "etransfer") {
            newBalances[transaction.accountType] -= transaction.amount;
          }
        });
        setBalances(newBalances);
      } else {
        throw new Error("Failed to load transactions");
      }
    } catch (error) {
      console.error("Error loading transactions:", error);
    }
  };

  const clearTransactions = async (accountNumber) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/DeleteAction.jsonld",
          ],
          "@type": "DeleteAction",
          Object: {
            "@context": [
              "http://schema4i.org/Thing.jsonld",
              "http://schema4i.org/Filter",
              "http://schema4i.org/DataLakeItem",
              "http://schema4i.org/UserAccount",
            ],
            "@type": "Filter",
            FilterItem: {
              "@type": "DataLakeItem",
              Creator: {
                "@type": "UserAccount",
                Identifier: localStorage.getItem("accountId"),
              },
              About: {
                "@type": "Transaction",
                accountNumber: accountNumber,
              },
            },
          },
        }),
      });
      if (response.ok) {
        setTransactions([]);
        setBalances({ checking: 0, savings: 0, creditCard: 0, investment: 0 });
      } else {
        throw new Error("Failed to clear transactions");
      }
    } catch (error) {
      console.error("Error clearing transactions:", error);
    }
  };

  const handleETransfer = ({ fromAccount, toContact, amount }) => {
    const accountNumber = localStorage.getItem("accountNumber");
    if (balances[fromAccount] < amount) {
      alert("Insufficient balance");
      return;
    }

    const newBalance = balances[fromAccount] - amount;

    const transaction = {
      type: "etransfer",
      accountNumber,
      amount,
      accountType: fromAccount,
      timestamp: new Date().toLocaleString(),
    };

    setBalances({
      ...balances,
      [fromAccount]: newBalance,
    });

    setTransactions((prevTransactions) => [...prevTransactions, transaction]);
    saveTransactionToAPI(transaction);
  };

  return (
    <div>
      <Carousel>
        <Carousel.Item style={{ maxHeight: "450px" }}>
          <img
            className="d-block w-100"
            src={CIBC}
            alt="Welcome to CIBC Bank"
            style={{ objectFit: "cover", maxHeight: "600px" }}
          />
        </Carousel.Item>
        <Carousel.Item style={{ maxHeight: "450px" }}>
          <img
            className="d-block w-100"
            src={CIBC}
            alt="Welcome to CIBC Bank"
            style={{ objectFit: "cover", maxHeight: "600px" }}
          />
        </Carousel.Item>
      </Carousel>

      <div className="text-center mb-4">
        <h2 className="display-4">
          Hi
          <span
            style={{
              color: "red",
              fontWeight: "bold",
              animation: "bounce-in-right 1s",
            }}
          >
            {" "}
            {firstName} {lastName}
          </span>{" "}
        </h2>
      </div>

      <Container className="my-5">
        <Row className="justify-content-center">
          {Object.keys(balances).map((accountType) => (
            <Col md={4} key={accountType}>
              <Card
                className="shadow"
                onMouseEnter={() => handleHover(accountType)}
                onMouseLeave={() => handleHover(null)}
              >
                <Card.Body>
                  <h5 className="card-title">
                    <span className="text-danger">
                      {accountType.charAt(0).toUpperCase() +
                        accountType.slice(1)}
                    </span>{" "}
                    Balance
                  </h5>
                  <p className="card-text">
                    ${balances[accountType].toFixed(2)}
                  </p>
                  {hoveredBalance === accountType && (
                    <>
                      <Button
                        variant="danger"
                        className="mt-2 me-2"
                        onClick={() => toggleTransactions(accountType)}
                      >
                        {visibleTransactions[accountType]
                          ? `Hide ${
                              accountType.charAt(0).toUpperCase() +
                              accountType.slice(1)
                            } Transactions`
                          : `Show ${
                              accountType.charAt(0).toUpperCase() +
                              accountType.slice(1)
                            } Transactions`}
                      </Button>
                    </>
                  )}
                </Card.Body>
              </Card>
            </Col>
          ))}
          <Col md={4}>
            <Card
              className="shadow"
              onMouseEnter={() => handleHover("total")}
              onMouseLeave={() => handleHover(null)}
            >
              <Card.Body>
                <h5 className="card-title">
                  <span className="text-danger">Total</span> Balance
                </h5>
                <p className="card-text">${totalBalance.toFixed(2)}</p>
                {hoveredBalance === "total" && (
                  <>
                    <Button
                      variant="danger"
                      className="mt-2 me-2"
                      onClick={toggleAllTransactions}
                    >
                      {visibleTransactions.total
                        ? "Hide All Transactions"
                        : "Show All Transactions"}
                    </Button>
                  </>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>

      {Object.keys(visibleTransactions).some(
        (accountType) => visibleTransactions[accountType]
      ) && (
        <Container className="my-5">
          <h3 className="mb-3">
            {currentAccountType === "total"
              ? "All Transactions"
              : `${
                  currentAccountType.charAt(0).toUpperCase() +
                  currentAccountType.slice(1)
                } Account Transactions`}
            :
          </h3>
          <div className="d-flex justify-content-center mt-2 mb-2">
            <Button
              variant="danger"
              className="me-2"
              onClick={handleLoadTransactions}
            >
              Load Transactions
            </Button>
            <Button variant="danger" onClick={handleClearTransactions}>
              Clear Transactions
            </Button>
          </div>

          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>#</th>
                <th>Date</th>
                <th>Type</th>
                <th>Amount</th>
                <th>Account Type</th>
                <th>Account Number</th>
              </tr>
            </thead>
            <tbody>
              {currentAccountType === "total"
                ? transactions.map((transaction, index) => (
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>{transaction.timestamp}</td>
                      <td>
                        {transaction.type
                          ? transaction.type.charAt(0).toUpperCase() +
                            transaction.type.slice(1)
                          : ""}
                      </td>
                      <td>
                        $
                        {transaction.amount
                          ? transaction.amount.toFixed(2)
                          : ""}
                      </td>
                      <td>
                        {transaction.accountType
                          ? transaction.accountType.charAt(0).toUpperCase() +
                            transaction.accountType.slice(1)
                          : ""}
                      </td>
                      <td>{transaction.accountNumber}</td>
                    </tr>
                  ))
                : transactions
                    .filter(
                      (transaction) =>
                        transaction.accountType === currentAccountType
                    )
                    .map((transaction, index) => (
                      <tr key={index}>
                        <td>{index + 1}</td>
                        <td>{transaction.timestamp}</td>
                        <td>
                          {transaction.type
                            ? transaction.type.charAt(0).toUpperCase() +
                              transaction.type.slice(1)
                            : ""}
                        </td>
                        <td>
                          $
                          {transaction.amount
                            ? transaction.amount.toFixed(2)
                            : ""}
                        </td>
                        <td>
                          {transaction.accountType
                            ? transaction.accountType.charAt(0).toUpperCase() +
                              transaction.accountType.slice(1)
                            : ""}
                        </td>
                        <td>{transaction.accountNumber}</td>
                      </tr>
                    ))}
            </tbody>
          </Table>
        </Container>
      )}

      <RewardOffer />
      <CongratulationsPage />

      <Container className="my-5">
        <Row className="d-flex align-items-stretch">
          <Col md={4} className="offset-md-1">
            <Card className="p-4 bg-light shadow rounded d-flex flex-column justify-content-between h-100">
              <div>
                <h2>
                  About <span className="text-danger">CIBC</span> Bank
                </h2>
                <p>Here you can provide information about CIBC Bank.</p>
                <h3>Our Services</h3>
                <p>Here you can list the services provided by CIBC Bank.</p>
              </div>
              <div className="d-grid gap-2">
                <Link to="/deposit" className="btn btn-danger btn-lg">
                  Deposit
                </Link>
                <Link to="/withdraw" className="btn btn-danger btn-lg">
                  Withdraw
                </Link>
                <Link to="/etransfer" className="btn btn-danger btn-lg">
                  E-Transfer
                </Link>
              </div>
            </Card>
          </Col>
          <Col md={7}>
            <img
              src={Content}
              alt="Welcome to CIBC Bank"
              className="img-fluid rounded h-100"
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default HomePage;
