// App.js
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  useLocation,
} from "react-router-dom";
import Navbar from "./Navbar";
import HomePage from "./HomePage";
import TransactionForm from "./TransactionForm";
import ETransferForm from "./ETransferForm";
import Login from "./Login";
import SignUp from "./SignUp";
import Footer from "./Footer";
import "bootstrap/dist/css/bootstrap.min.css";

const accountNumber = localStorage.getItem("accountNumber");
const accountid = 'USERID-' + accountNumber

const apiConfig = {
  url: "https://json-storage-api.p.rapidapi.com/datalake",
  headers: {
    "content-type": "application/json",
    "X-RapidAPI-Key": "737b5b8023msh5dc8759b04faf33p1be655jsn98f86fc2f298",
    "X-RapidAPI-Host": "json-storage-api.p.rapidapi.com",
  },
};

const App = () => {
  const [balances, setBalances] = useState({
    checking: 0,
    savings: 0,
    creditCard: 0,
    investment: 0,
  });
  const [transactions, setTransactions] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const contacts = [
    "Abhishek Dhiman",
    "Sukhjeet Singh",
    "Arpan Silwal",
    "Nawaz Chowdhry",
    "Surjeet Singh",
    "Sejal Josan",
    "Nabdeep Kaur",
    "Jasdeep Kaur",
    "Riya Mankotia",
  ];

  useEffect(() => {
    const authState = localStorage.getItem("isAuthenticated");
    if (authState) {
      setIsAuthenticated(JSON.parse(authState));
      const accountNumber = localStorage.getItem("accountNumber");
      if (accountNumber) {
        loadTransactions(accountNumber);
      }
    }

    const lastVisitedRoute = localStorage.getItem("lastVisitedRoute");
    if (lastVisitedRoute) {
      window.history.replaceState(null, "", lastVisitedRoute);
    }
  }, []);

  const saveTransactionToAPI = async (transaction) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/CreateAction.jsonld",
          ],
          "@type": "CreateAction",
          Result: {
            "@context": [
              "http://schema4i.org/DataLakeItem.jsonld",
              "http://schema4i.org/UserAccount.jsonld",
              'http://schema4i.org/OfferForPurchase.jsonld',
              'http://schema4i.org/Offer.jsonld',
              'http://schema4i.org/Organization.jsonld',
              'http://schema4i.org/PostalAddress.jsonld'
            ],
            "@type": "DataLakeItem",
            Name: "Transaction",
            Creator: {
              "@type": "UserAccount",
              Identifier: accountid
            },
            About: {
              "@type": "Organization",
            },
            Type: transaction.type,
            timestamp: transaction.timestamp,
            amount:transaction.amount,
            accountNumber: transaction.accountNumber,
            accountType: transaction.accountType
          },
        }),
      });
      if (!response.ok) {
        throw new Error("Failed to save transaction");
      }
    } catch (error) {
      console.error("Error saving transaction:", error);
    }
  };

  const loadTransactions = async (accountNumber) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/SearchAction.jsonld",
          ],
          "@type": "SearchAction",
          Object: {
            "@context": [
              "http://schema4i.org/Thing.jsonld",
              "http://schema4i.org/Filter",
              "http://schema4i.org/DataLakeItem",
              "http://schema4i.org/UserAccount",
            ],
            "@type": "Filter",
            FilterItem: {
              "@type": "DataLakeItem",
              Creator: {
                "@type": "UserAccount",
                Identifier:accountid
              },
            },
          },
        }),
      });
      if (response.ok) {
        const data = await response.json();
        const transactions = data.Result.ItemListElement.map(
          (item) => item.Item.About
        );
        setTransactions(transactions);
        const newBalances = {
          checking: 0,
          savings: 0,
          creditCard: 0,
          investment: 0,
        };
        transactions.forEach((transaction) => {
          if (transaction.type === "deposit") {
            newBalances[transaction.accountType] += transaction.amount;
          } else if (transaction.type === "withdraw" || transaction.type === "etransfer") {
            newBalances[transaction.accountType] -= transaction.amount;
          }
        });
        setBalances(newBalances);
      } else {
        throw new Error("Failed to load transactions");
      }
    } catch (error) {
      console.error("Error loading transactions:", error);
    }
  };

  const clearTransactions = async (accountNumber) => {
    try {
      const response = await fetch(apiConfig.url, {
        method: "POST",
        headers: apiConfig.headers,
        body: JSON.stringify({
          "@context": [
            "http://schema4i.org/Thing.jsonld",
            "http://schema4i.org/Action.jsonld",
            "http://schema4i.org/DeleteAction.jsonld",
          ],
          "@type": "DeleteAction",
          Object: {
            "@context": [
              "http://schema4i.org/Thing.jsonld",
              "http://schema4i.org/Filter",
              "http://schema4i.org/DataLakeItem",
              "http://schema4i.org/UserAccount",
            ],
            "@type": "Filter",
            FilterItem: {
              "@type": "DataLakeItem",
              Creator: {
                "@type": "UserAccount",
                Identifier: accountid
              },
            },
          },
        }),
      });
      if (response.ok) {
        setTransactions([]);
        setBalances({ checking: 0, savings: 0, creditCard: 0, investment: 0 }); // Reset balances
      } else {
        throw new Error("Failed to clear transactions");
      }
    } catch (error) {
      console.error("Error clearing transactions:", error);
    }
  };

  const handleTransaction = ({ amount, type, accountType }) => {
    const accountNumber = localStorage.getItem("accountNumber");
    const transaction = {
      type,
      accountNumber,
      amount,
      accountType,
      timestamp: new Date().toLocaleString(),
    };

    let newBalance;
    if (type === "deposit") {
      newBalance = balances[accountType] + amount;
    } else if (type === "withdraw") {
      if (balances[accountType] < amount) {
        alert("Insufficient balance");
        return;
      }
      newBalance = balances[accountType] - amount;
    }

    setBalances({
      ...balances,
      [accountType]: newBalance,
    });

    setTransactions((prevTransactions) => [...prevTransactions, transaction]);
    saveTransactionToAPI(transaction); // Save transaction to API
  };

  const handleETransfer = ({ fromAccount, toContact, amount }) => {
    const accountNumber = localStorage.getItem("accountNumber");
    if (balances[fromAccount] < amount) {
      alert("Insufficient balance");
      return;
    }

    const newBalance = balances[fromAccount] - amount;

    const transaction = {
      type: "etransfer",
      accountNumber, // Use account number from local storage
      amount,
      accountType: fromAccount,
      timestamp: new Date().toLocaleString(),
    };

    setBalances({
      ...balances,
      [fromAccount]: newBalance,
    });

    setTransactions((prevTransactions) => [...prevTransactions, transaction]);
    saveTransactionToAPI(transaction); // Save transaction to API
  };

  const handleLogin = (accountNumber) => {
    setIsAuthenticated(true);
    localStorage.setItem("isAuthenticated", true);
    localStorage.setItem("accountNumber", accountNumber);
    loadTransactions(accountNumber); // Load transactions for logged-in user
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem("isAuthenticated");
    localStorage.removeItem("accountNumber"); // Clear account number on logout
    setTransactions([]); // Clear transactions on logout
    setBalances({ checking: 0, savings: 0, creditCard: 0, investment: 0 }); // Reset balances
  };

  const ProtectedRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />;
  };

  const RouteChangeHandler = () => {
    const location = useLocation();

    useEffect(() => {
      localStorage.setItem("lastVisitedRoute", location.pathname);
    }, [location]);

    return null;
  };

  return (
    <Router>
      <div>
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />
        <div className="container mt-4">
          <RouteChangeHandler />
          <Routes>
            <Route
              path="/"
              element={
                isAuthenticated ? (
                  <HomePage
                    balances={balances}
                    transactions={transactions}
                    onLoadTransactions={loadTransactions}
                    onClearTransactions={clearTransactions}
                    firstName={localStorage.getItem("firstName")}
                    lastName={localStorage.getItem("lastName")}
                  />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/deposit"
              element={
                isAuthenticated ? (
                  <TransactionForm type="deposit" onSubmit={handleTransaction} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/withdraw"
              element={
                isAuthenticated ? (
                  <TransactionForm type="withdraw" onSubmit={handleTransaction} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/etransfer"
              element={
                isAuthenticated ? (
                  <ETransferForm contacts={contacts} onSubmit={handleETransfer} />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/login"
              element={<Login onLogin={handleLogin} isAuthenticated={isAuthenticated} />}
            />
            <Route path="/signup" element={<SignUp />} />
          </Routes>
        </div>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
